$(document).ready(function(){
	AOS.init({ disable: 'mobile' });
});
$(document).ready(function(){
    $(window).resize(function(){
        fixHeight();
    });
    fixHeight();
	document.onkeyup = function(e) {
		switch(e.which) {
			case 81: play("Q"); break;
			case 87: play("W"); break;
			case 69: play("E"); break;
			case 65: play("A"); break;
			case 83: play("S"); break;
			case 68: play("D"); break;
			case 90: play("Z"); break;
			case 88: play("X"); break;
			case 67: play("C"); break;
		}
	};
});
let play = function(key){
	let words = $("#" + key)[0].src.split("/")[$("#" + key)[0].src.split("/").length-1].split(".wav")[0].split("_");
	for(let idx = 0; idx < words.length; idx++){
		words[idx] = words[idx].charAt(0).toUpperCase() + words[idx].substr(1);
	}
    $("#display")[0].innerHTML = words.join(" ");
    $("#" + key)[0].play();
};
let fixHeight = function(){
    $(".drum-pad").css("height", $(".drum-pad")[0].offsetWidth);
};
